using System;
using System.Runtime.InteropServices;
using UnityEngine;
using Newtonsoft.Json;


public class ParityScaleCodec : MonoBehaviour
{
    //public static CyborNFTState GLobalCyborNFTState { get; set; }

    #if UNITY_STANDALONE_WIN
    [DllImport("state_codec.dll")]
    #elif UNITY_STANDALONE_OSX
    [DllImport("libstate_codec.dylib")]
    #endif
    private static extern IntPtr hex_to_state_json(string hexStr);

    #if UNITY_STANDALONE_WIN
    [DllImport("state_codec.dll")]
    #elif UNITY_STANDALONE_OSX
    [DllImport("libstate_codec.dylib")]
    #endif
    private static extern void free_c_string(IntPtr ptr);

    public static string ConvertHexToStateJson(string hexStr)
    {
        try
        {
            IntPtr ptr = hex_to_state_json(hexStr);
            string jsonStr = Marshal.PtrToStringAnsi(ptr);
            // UnityEngine.Debug.Log("Claim------------- ::::" + jsonStr);
            free_c_string(ptr);
            return jsonStr;
        }
        catch (Exception ex)
        {
            Console.WriteLine($"Error: {ex.Message}");
            UnityEngine.Debug.LogError(ex);
            return string.Empty;
        }
    }

    void Start() {
        string r = ConvertHexToStateJson("");
        // UnityEngine.Debug.Log("Claim1 ::::" + r);

        CyborNFTFromJson CC = (CyborNFTFromJson)JsonConvert.DeserializeObject(r, typeof(CyborNFTFromJson));

        // // CC = JsonUtility.FromJson<CyborNFTFromJson>(r);
        UnityEngine.Debug.Log("Claim1 :::: owner_by_id " + CC.owner_by_id.Count);
        UnityEngine.Debug.Log("Claim1 :::: token_approvals " + CC.token_approvals.Count);
        UnityEngine.Debug.Log("Claim1 :::: token_metadata_by_id " + CC.token_metadata_by_id.Count);
        UnityEngine.Debug.Log("Claim1 :::: tokens_for_owner " + CC.tokens_for_owner.Count);
        UnityEngine.Debug.Log("Claim1 :::: is_gaming " + CC.is_gaming.Count);
        UnityEngine.Debug.Log("Claim1 :::: name " + CC.collection.name);
        UnityEngine.Debug.Log("Claim1 :::: level " + CC.level);
  

        GLobalCyborNFTState = CC.convertReadable();
        UnityEngine.Debug.Log("Claim1 OBJECT ::::" + JsonUtility.ToJson(ParityScaleCodec.GLobalCyborNFTState, true));
    }
}
